import Board from './board';
import { Card } from './card';
import { assert, should, expect } from 'chai';

describe('Board', () => {
  const board = new Board();

  describe('compared cards', () => {
    const cards = [new Card('A'), new Card('B')]
    it('should be same', () => {
      assert.equal(
        board.compareCards(cards, 0, 0),
        true
      )
    });

    it('should not be same', () => {
      assert.equal(
        board.compareCards(cards, 0, 1),
        false
      )
    });
  });

  describe('cards template', () => {
    it('should have same members', () => {
      const new_board = new Board();
      assert.sameDeepMembers(board.cardsTemplate, new_board.cardsTemplate);
    });

    it('should have 16 cards', () => {
      expect(board).to.have.property('cardsTemplate').with.length(16);
    });
  });

  describe('shuffle cards', () => {
    it('should have 16 members', () => {
      assert.lengthOf(board.shuffleCards(), 16);
    });

    it('should have all template cards', () => {
      expect(board.shuffleCards()).to.have.members(board.cardsTemplate);
    });

    it('should be shuffeled', () => {
      const setOne = board.shuffleCards();
      const setTwo = board.shuffleCards();
      assert.notEqual(setOne, setTwo);
    });
  });
});

import { Card } from './card'


class Board {
  constructor() {
    this.cardsTemplate = [
      new Card('A'),
      new Card('A'),
      new Card('B'),
      new Card('B'),
      new Card('C'),
      new Card('C'),
      new Card('D'),
      new Card('D'),
      new Card('E'),
      new Card('E'),
      new Card('F'),
      new Card('F'),
      new Card('G'),
      new Card('G'),
      new Card('H'),
      new Card('H')
    ];
  }

  compareCards(cards, indexCardOne, indexCardTwo) {
    if (cards[indexCardOne].value === cards[indexCardTwo].value) {
      return true;
    } else {
      return false;
    }
  }

  shuffleCards() {
    let positions = []
    let cards = [];
    let randomIndex;

    while (cards.length !== this.cardsTemplate.length) {
      randomIndex = Math.floor(this.cardsTemplate.length * Math.random());
      if (!positions.includes(randomIndex)) {
        positions.push(randomIndex);
        cards.push(this.cardsTemplate[randomIndex]);
      }
    }

    return cards
  }
}

export default Board;

export class Card {
  constructor(value) {
    this._value = value;
  }

  get value() {
    return this._value
  }
}

