import Board from '../board/board';
import { Card } from '../board/card';
import Game from './game';
import { assert, should, expect } from 'chai';
import UserInterface from '../user-interface/user-interface';

describe('Game', () => {
  let game = new Game();
  const board = new Board()

  beforeEach(() => {
    game = new Game();
  });

  describe('sleep functions', () => {
    it('should pass without error', async () => {
      await game.sleep(500);
      assert.equal(true, true);
    });
  });

  describe('set board', () => {
    it('should be same', () => {
      game.setBoard(board);
      assert.deepEqual(game.board, board);
    });
  });

  describe('restarted attrs', () => {
    game.moves = 5;
    game.flips = 5;
    game.matchedPairs = 5;
    game.firstCard = 5;

    it('moves should be equal 0', () => {
      assert.equal(game.moves, 0);
    });

    it('flips should be equal 0', () => {
      assert.equal(game.flips, 0);
    });

    it('matched pairs should be equal 0', () => {
      assert.equal(game.matchedPairs, 0);
    });

    it('first card should be undefined', () => {
      assert.notExists(game.firstCard);
    });
  });

  describe('check prospects', () => {
    it('if null should return false', () => {
      assert.isFalse(game.checkProspect(null));
    });

    it('if first card should return false', () => {
      game.firstCard = 1;
      assert.isFalse(game.checkProspect(1))
    });

    it('if flips are grater then one should return false', () => {
      game.flips = 2;
      assert.isFalse(game.checkProspect(2));
    })
  });

  describe('cards matched', () => {
    it('matched pairs should rised', () => {
      let matchedPairs = game.matchedPairs;
      game.cardsMatched(1, 2);
      assert.isAbove(game.matchedPairs, matchedPairs);
    })

    it('first card should be undefined', () => {
      game.cardsMatched(1, 2);
      assert.notExists(game.firstCard);
    });

    it('flips should be equal 0', () => {
      game.cardsMatched(1, 2);
      assert.equal(game.flips, 0);
    });
  });

  describe('cards not matched', () => {
    it('matched pairs should not rised', () => {
      let matchedPairs = game.matchedPairs;
      game.cardsNotMatched(1, 2);
      assert.equal(game.matchedPairs, matchedPairs);
    })

    it('first card should be undefined', () => {
      game.cardsNotMatched(1, 2);
      assert.notExists(game.firstCard);
    });

    it('flips should be equal 0', () => {
      game.cardsNotMatched(1, 2);
      assert.equal(game.flips, 0);
    });
  });
});

const MAX_MATCH_PAIRS = 8;

class Game {
  constructor() {
    this.board = null;
    this.userInterface = null;
    this.cards = [];
    this.moves = 0;
    this.flips = 0;
    this.matchedPairs = 0;
    this.firstCard = undefined;
    this.gameProgress = false;
  }

  sleep(ms) {
    return new Promise(resolve => setTimeout(resolve, ms));
  }

  setBoard(board) {
    this.board = board;
  }

  setUI(userInterface) {
    this.userInterface = userInterface;
  }

  resetAttr() {
    this.moves = 0;
    this.flips = 0;
    this.matchedPairs = 0;
    this.firstCard = undefined;
  }

  startNewGame() {
    this.resetAttr();
    this.cards = this.board.shuffleCards();
    this.userInterface.resetMovesCounter();
    this.userInterface.buildBoard(this.cards);
    this.userInterface.enableRestartButton();
    this.userInterface.startTime();
  }

  checkProspect(cardIndex) {
    if (cardIndex === null) {
      return false;
    }
    if (this.firstCard === cardIndex) {
      return false;
    }
    if (this.flips > 1) {
      return false;
    }
    if (this.userInterface.isMatched(cardIndex)) {
      return false;
    }
    return true;
  }

  async turn(cardIndex) {
    if (!this.checkProspect(cardIndex)) {
      return false;
    }

    this.userInterface.cardFaceUp(cardIndex);
    this.flips += 1;

    if (this.flips === 1) {
      this.firstCard = cardIndex;
    } else {
      this.moves += 1;
      this.userInterface.updateMovesCounter(this.moves);
      if (!this.board.compareCards(this.cards, this.firstCard, cardIndex)) {
        this.cardsNotMatched(this.firstCard, cardIndex);
      } else {
        this.cardsMatched(this.firstCard, cardIndex);
      }
    }

    if (this.matchedPairs >= MAX_MATCH_PAIRS) {
      this.userInterface.stopTime();
      await this.sleep(2000);
      this.userInterface.showWinBoard(this, this.moves);
      return true;
    }

    return false;
  }

  async cardsMatched(cardOneIndex, cardTwoIndex) {
    this.matchedPairs += 1;
    await this.sleep(600);
    this.userInterface.matchCards(cardOneIndex, cardTwoIndex);
    this.firstCard = undefined;
    this.flips = 0;
  }

  async cardsNotMatched(cardOneIndex, cardTwoIndex) {
    await this.sleep(1000);
    this.userInterface.cardFaceDown(cardOneIndex);
    this.userInterface.cardFaceDown(cardTwoIndex);
    this.firstCard = undefined;
    this.flips = 0;
  }
}

export default Game;

class UserInterface {
  constructor() {
    this.time = null;
    this.timeMin = 0;
    this.timeSec = 0;
    this.timer = document.getElementsByClassName('timer')[0];
    this.moveCounter = document.getElementsByClassName('moves')[0];
  }

  clearBoard() {
    const boardDOM = document.getElementsByClassName('cards')[0];
    if (boardDOM.childElementCount > 0) {
      while (boardDOM.firstChild) {
        boardDOM.removeChild(boardDOM.firstChild);
      }
    }
  }

  buildBoard(cards) {
    this.clearBoard();
    const boardDOM = document.getElementsByClassName('cards')[0];
    cards.forEach((card, index) => {
      const liElement = document.createElement('li');
      liElement.setAttribute('id', `${index}`);
      liElement.setAttribute('class', 'card');
      const spanElement = document.createElement('span');
      spanElement.setAttribute('class', 'parag');
      spanElement.innerHTML = card.value;
      liElement.appendChild(spanElement);
      boardDOM.appendChild(liElement);
    })
  }

  cardFaceUp(cardIndex) {
    const card = document.getElementById(cardIndex);
    card.setAttribute('class', 'card awers');
  }

  cardFaceDown(cardIndex) {
    const card = document.getElementById(cardIndex);
    card.setAttribute('class', 'card rewers');
  }

  matchCards(cardOneIndex, cardTwoIndex) {
    const cardOne = document.getElementById(cardOneIndex);
    const cardTwo = document.getElementById(cardTwoIndex);
    cardOne.setAttribute('class', 'card matched');
    cardTwo.setAttribute('class', 'card matched');
  }

  isMatched(cardIndex) {
    return document.getElementById(cardIndex).getAttribute('class').includes('matched')
  }

  updateMovesCounter(moves) {
    this.moveCounter.innerHTML = `${moves} moves`;
  }

  resetMovesCounter() {
    this.moveCounter.innerHTML = `0 moves`;
  }

  startTime() {
    this.stopTime();
    this.timeMin = 0;
    this.timeSec = 0;
    this.timer.innerHTML = `${this.timeMin}:${this.timeSec}`;
    this.time = setInterval(this.updateTime, 1000, this);
  }

  stopTime() {
    if (this.time) {
      clearInterval(this.time);
      this.time = null;
    }
  }

  updateTime(ui) {
    ui.timeSec += 1;
    if (ui.timeSec >= 60) {
      ui.timeSec = 0;
      ui.timeMin += 1;
    }
    ui.timer.innerHTML = `${ui.timeMin}:${ui.timeSec}`;
  }

  disableRestartButton() {
    const button = document.getElementsByClassName('restart')[0];
    button.disabled = true;
  }

  enableRestartButton() {
    const button = document.getElementsByClassName('restart')[0];
    button.disabled = false;
  }

  showWinBoard(game, moves) {
    this.disableRestartButton()
    this.blurGame();
    this.stopTime();
    const winTimer = document.getElementById('timer');
    const winMoves = document.getElementById('moves');
    winTimer.innerHTML = `${this.timeMin}:${this.timeSec}`;
    winMoves.innerHTML = `${moves}`;

    const winButton = document.getElementsByClassName('win-button')[0];
    winButton.game = game;
    winButton.addEventListener('click', this.setup);
    document.getElementsByClassName('win-board')[0].setAttribute('style', 'display: flex;');
  }

  blurGame() {
    const game = document.getElementsByClassName('game')[0];
    game.setAttribute('style', '-webkit-filter: blur(12px);');
  }

  setup(event) {
    document.getElementsByClassName('win-board')[0].setAttribute('style', 'display: none;');
    document.getElementsByClassName('game')[0].setAttribute('style', '-webkit-filter: none;');
    event.target.game.startNewGame();
  }
}

export default UserInterface;
